css:
.print-format table, .print-format tr, 
.print-format td, .print-format div, .print-format p {
	font-family: Monospace;
	line-height: 200%;
	vertical-align: middle;
}
@media screen {
	.print-format {
		width: 4in;
		padding: 0.25in;
		min-height: 4in;
	}
}

.title {
    text-align: center;
}


input[type="checkbox"] {
    transform:scale(1.5, 1.5);
}
